ModuleMappings.Scss:
$mobile-breakpoint: 768px;
$xl-breakpoint: 1200px;
$spacer: 1rem;

margin: 2rem 0;

.headline {
    padding-bottom: ($spacer * 2);
    font-size: 34px;
    font-weight: bold;
}

.location {
    position: relative;
    display: inline-block;
    padding: 16px;
    padding-left: 0px !important;
    height: 320px;
    width: 360px;
    text-align: left;

    .name {
        font-size: 24px;
        font-weight: 600;
    }

    .opentoday {
        padding-top: ($spacer * 0.5) !important;
        font-size: 16px;
        font-weight: bold;
    }

    .address {
        padding-top: ($spacer * 0.5) !important;
        font-size: 16px;
    }

    .phone {
        font-size: 16px;

        a {
            color: #0041ff;
        }
    }

    .details {
        padding-top: ($spacer * 0.5) !important;
        font-size: 16px;
        --details-xl-length: 110;
        --details-lg-length: 100;
    }

    .cta {
        font-size: 16px;
        color: #0041ff;
        line-height: 1.75;
        letter-spacing: 1.5px;
        text-decoration: none;
        border-bottom: 1px solid #0041ff;
        padding-bottom: 3px;
        position: absolute;
        bottom: 16px;
    }
}

@media (max-width: $mobile-breakpoint) {
    .headline {
        text-align: center;
    }
    .location-col {
        text-align: center;
    }

    .location {
        border-bottom: 1px solid;
        padding-top: 10px;
    }

    .location-col:last-child {
        .location {
            border-bottom: 0px;
        }
    }
}

@media (max-width: $xl-breakpoint) {
    .headline {
        font-size: 24px;
        padding-bottom: 0rem;
    }

    .location {
        height: 290px;
        width: 270px;

        .name {
            font-size: 20px;
        }

        .opentoday, .address, .phone, .details {
            font-size: 14px;
        }
    }
}

